njectionStrategy;import org.apache.sling.models.annotations.injectorspecific.SlingObject;import org.apache.sling.models.annotations.injectorspecific.ValueMapValue;import org.slf4j.Logger;import org.slf4j.LoggerFactory;@Model(adaptables = {    SlingHttpServletRequest.class}, adapters = {    Header.class,    ComponentExporter.class}, resourceType = "onecms/components/content/header")@Exporter(name = ExporterConstants.SLING_MODEL_EXPORTER_NAME, extensions = ExporterConstants.SLING_MODEL_EXTENSION)public class HeaderImpl        implements Header {    @ChildResourceFromRequest(injectionStrategy = InjectionStrategy.OPTIONAL)    @JsonIgnore    private List<NavItems> navItems;    @ValueMapValue(injectionStrategy = InjectionStrategy.OPTIONAL)    private String brandLogo;    @ValueMapValue(injectionStrategy = InjectionStrategy.OPTIONAL)    private String brandLogoAltText;    @ValueMapValue(injectionStrategy = InjectionStrategy.OPTIONAL)    private String brandDivision;    @ChildResourceFromRequest(injectionStrategy = InjectionStrategy.OPTIONAL)    private List<ActionIcons> actions;    @ChildResourceFromRequest(injectionStrategy = InjectionStrategy.OPTIONAL)    private List<Megamenu> megamenuItems;    @ValueMapValue(injectionStrategy = InjectionStrategy.OPTIONAL)    private String title;    @ValueMapValue(injectionStrategy = InjectionStrategy.OPTIONAL)    private String description;    @ValueMapValue(injectionStrategy = InjectionStrategy.OPTIONAL)    private String productUrl;    @ValueMapValue(injectionStrategy = InjectionStrategy.OPTIONAL)    private String target;    @ChildResourceFromRequest(injectionStrategy = InjectionStrategy.OPTIONAL)    private Image featuredProductImage;        @SlingObject    private Resource resource;    private NavigationData navigationData;    private BrandData brandData;        private FeaturedProduct featuredProduct;    private String jsonData;    private final Logger LOGGER = LoggerFactory.getLogger(FooterImpl.class);    @PostConstruct    protected void init() {        LOGGER.info("Starting init method of Header");        ObjectMapper objectMapper = new ObjectMapper();        try {            navigationData = new NavigationData();            navigationData.setNavItems(navItems);            brandData = new BrandData();            brandData.setActions(actions);            brandData.setBrandDivision(brandDivision);            brandData.setBrandLogo(brandLogo);            brandData.setBrandLogoAltText(brandLogoAltText);            featuredProduct = new FeaturedProduct();            featuredProduct.setDescription(description);            featuredProduct.setProductUrl(productUrl);            featuredProduct.setTitle(title);            featuredProduct.setTarget(target);            featuredProduct.setImageUrl(featuredProductImage.getFileReference());            jsonData = objectMapper.writeValueAsString(this);        } catch (JsonProcessingException ex) {            LOGGER.error("Error in parsing JSON Data for Header component {}", ex.getMessage());        }        LOGGER.info("Exiting init method of Header");    }    @Override    @JsonIgnore    public List<NavItems> getNavItems() {        return navItems != null ? Collections.unmodifiableList(navItems) : null;    }    @Override    @JsonIgnore    public String getBrandLogo() {        return brandLogo;    }    @Override    @JsonIgnore    public String getBrandLogoAltText() {        return brandLogoAltText;    }    @Override    @JsonIgnore    public String getBrandDivision() {        return brandDivision;    }    @Override    @JsonIgnore    public List<ActionIcons> getActions() {        return actions != null ? Collections.unmodifiableList(actions) : null;    }    @Override    @JsonIgnore    public String getExportedType() {        return resource.getResourceType();    }    @JsonIgnore    @Override    public String getJsonData() {        return jsonData;    }    @Override    @JsonProperty(value = "navData")    public NavigationData getNavigationData() {        return navigationData;    }    @Override    public BrandData getbrandData() {        return brandData;    }    public List<Megamenu> getMegamenuItems() {        return megamenuItems;    }    @Override    @JsonIgnore    public String getTitle() {        return title;    }    @Override    @JsonIgnore    public String getDescription() {        return description;    }    @JsonIgnore    public Image getFeaturedProductImage() {        return featuredProductImage;    }    @Override    public FeaturedProduct getFeaturedProduct() {        return featuredProduct;    }}